import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from 'react';
import { Platform, Text, View, StyleSheet } from 'react-native';
import * as Location from 'expo-location';
import { initializeApp } from "firebase/app";
import firebase from 'firebase'


export default function App() {

  const [locationLat, setLocationLat] = useState(null);
  const [locationLong, setLocationLong] = useState(null)
  const [errorMsg, setErrorMsg] = useState(null);
  const [address, setAdress] = useState('');
  const [clicked, setClicked] = useState(false)

  const firebaseConfig = {
    apiKey: "AIzaSyBw0JiynMCdwmvU4R9HuB6mCOXCmwR8H0E",
    authDomain: "locationapp-3efcb.firebaseapp.com",
    projectId: "locationapp-3efcb",
    storageBucket: "locationapp-3efcb.appspot.com",
    messagingSenderId: "1020753340007",
    appId: "1:1020753340007:web:31d1eeac64004d46bdd6c2"
  };

  // Initialize Firebase
  if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
  }
   var db = firebase.firestore();

  let fireAddress = db.collection("test").doc("locWaWWxNtucZmAIU5py")


  useEffect(() => {
    (async () => {
      let { status } = await Location.requestForegroundPermissionsAsync();
      if (status !== 'granted') {
        setErrorMsg('Permission to access location was denied');
        return;
      }

      let location = await Location.getCurrentPositionAsync({});
      setLocationLat(location.coords.latitude);
      setLocationLong(location.coords.longitude);



      let result = await fetch('https://httpbin.org/post', {
          method: 'post',
          mode: 'no-cors',
          headers: {
            'Accept': 'application/json',
            'Content-type': 'application/json'
          },
          body: JSON.stringify(location)

      })
  fireAddress.set({
    location:[ address ]
  })
  .then(() => {
    console.log("Document successfully written!");
  })
  .catch((error) => {
    console.error("Error writing document: ", error);
  });
      console.log(result)
      console.log(location.coords)
    })();
  }, []);


  let text = 'Waiting..';
  if (errorMsg) {
    text = errorMsg;
  } else if (location) {
    text = JSON.stringify(location);
  }

  function showLocation(){
    fetch('https://maps.googleapis.com/maps/api/geocode/json?address=' + locationLat + ',' + locationLong + '&key=' + 'AIzaSyANgMRwJSiVKGPdjshbjuavIYRuOg7dKvQ ')
     .then((response) => response.json())
     .then((responseJson) => {
      setAdress(responseJson.results[0].formatted_address)
      setClicked(true)
       console.log(address)

   })

  fireAddress.update({
    regions: firebase.firestore.FieldValue.arrayUnion(address)
})
.then(() => {
  console.log("Document successfully updated!");
})
.catch((error) => {
  console.error("Error writing document: ", error);
});

   }







  return (
    <View style={styles.container}>
      <Text>Open up App.js to start working on your app!</Text>
      <Text>Open up App.js to start working on your app!</Text>
      <button onClick={showLocation}>Show my location</button>
      {console.log(address)}
      {clicked == true ?
       <p>You are located at: {address}</p> :null}
      {/* {address.map((item) =>{

      return(<View>
      <Text> address: {item.formatted_address}</Text>

      </View>
      )
    })} */}

      <Text>Open up App.js to start working on your app!</Text>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
