import { setStatusBarHidden, StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from 'react';
import Button from './components/Button'
import { Platform, Text, View, StyleSheet } from 'react-native';
import * as Location from 'expo-location';
import { initializeApp } from "firebase/app";
import firebase from 'firebase';


export default function App() {

  const [locationLat, setLocationLat] = useState(null);
  const [locationLong, setLocationLong] = useState(null)
  const [errorMsg, setErrorMsg] = useState(null);
  const [address, setAddress] = useState('');
  const [clicked, setClicked] = useState(false);
  const [lastLocation, setLastLocation] = useState([])

  const firebaseConfig = {
    apiKey: "AIzaSyBw0JiynMCdwmvU4R9HuB6mCOXCmwR8H0E",
    authDomain: "locationapp-3efcb.firebaseapp.com",
    projectId: "locationapp-3efcb",
    storageBucket: "locationapp-3efcb.appspot.com",
    messagingSenderId: "1020753340007",
    appId: "1:1020753340007:web:31d1eeac64004d46bdd6c2"
  };

  // Initialize Firebase
  if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
  }
   var db = firebase.firestore();

  let fireAddress = db.collection("test").doc("locWaWWxNtucZmAIU5py")


  useEffect(() => {
    (async () => {
      let { status } = await Location.requestForegroundPermissionsAsync();
      if (status !== 'granted') {
        setErrorMsg('Permission to access location was denied');
        return;
      }

      let location = await Location.getCurrentPositionAsync({});
      setLocationLat(location.coords.latitude);
      setLocationLong(location.coords.longitude);



      let result = await fetch('https://httpbin.org/post', {
          method: 'post',
          mode: 'no-cors',
          headers: {
            'Accept': 'application/json',
            'Content-type': 'application/json'
          },
          body: JSON.stringify(location)

      })



      console.log(result)
      console.log(location.coords)
    })();
  }, []);


  let text = 'Waiting..';
  if (errorMsg) {
    text = errorMsg;
  } else if (location) {
    text = JSON.stringify(location);
  }
  var randoms = ['Yes', 'No', 'Maybe', 'random'];

  function showLocation(){
    fetch('https://maps.googleapis.com/maps/api/geocode/json?address=' + locationLat + ',' + locationLong + '&key=' + 'AIzaSyANgMRwJSiVKGPdjshbjuavIYRuOg7dKvQ ')
     .then((response) => response.json())
     .then((responseJson) => {
      var random = randoms[Math.floor(Math.random() * randoms.length)];
      setAddress(responseJson.results[0].formatted_address + random)
      setClicked(true)
       console.log(address)

   })
   fireAddress.set({
    location: address 
  })
  .then(() => {
    console.log("Document successfully written!");
  })
  .catch((error) => {
    console.error("Error writing document: ", error);
  });

  fireAddress.update({
    location: firebase.firestore.FieldValue.arrayUnion(address)
})
.then(() => {
  console.log("Document successfully updated!");
})
.catch((error) => {
  console.error("Error writing document: ", error);
});
let coll = db.collection("test")
.get()
    .then((querySnapshot) => {
        querySnapshot.forEach((doc) => {
            // doc.data() is never undefined for query doc snapshots
            console.log(doc.id, " => ", doc.data().location);
            setLastLocation(doc.data().location)
            console.log(lastLocation)
        });
    })
    .catch((error) => {
        console.log("Error getting documents: ", error);
    });
console.log(coll)

   }







  return (
    <View style={styles.container}>
      <Button setAddress={setAddress} 
              address={address}
              setLastLocation />
      <Text>Open up App.js to start working on your app!</Text>
      <Text>Open up App.js to start working on your app!</Text>
      <button onClick={showLocation}>Show my location</button>
      {console.log(lastLocation)}
      {clicked == true ?
       <p>You are located at: {address}</p> :null}
      {lastLocation.map((item) =>{

      return(<View>
      <Text> Your last location: {item}</Text>

      </View>
      )
    })}

      <Text>Open up App.js to start working on your app!</Text>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
